import createStore from '../store/store'
const isServer = typeof window === 'dndefined'
const __NEXT_REDUX_STORE__  = '__NEXT_REDUX_STORE__'

function getOrCreateStore (initialState) {
    if(isServer) {
        return createStore(initialState)
    }

    if (!window[__NEXT_REDUX_STORE__]) {
        window[__NEXT_REDUX_STORE__] = createStore(initialState)
    }
    return window[__NEXT_REDUX_STORE__]
}


export default (Comp) => {
   
    function TestHocComp ({ Component, pageProps, ...rest}) {
        console.log(Component, pageProps)
        if (pageProps) {
            pageProps.test = '123'
        }
        return <Comp Component = {Component} pageProps={pageProps} {...rest} />
    }

    //TestHocComp.getInitialProps = Comp.getInitialProps
    TestHocComp.getInitialProps = async (ctx)=> {
        let appProps = {}
        if (typeof Comp.getInitialProps === 'function') {
            appProps = await Comp.getInitialProps(ctx)
        }

        const reduxStore = getOrCreateStore()

        return {
            ...appProps,
            
        }

    }

    return TestHocComp 
}
