import React from 'react'
import Router,{ withRouter } from 'next/router'

const Demo = (props) => {
    return(
        <>
            {console.log(props.router.id)}
            <button onClick={()=>Router.push('/')} >送我去主页</button>
            <div>这里是demo页</div>
            <div>id='{props.router.query.id}'</div>
        </>
    )
}
export default withRouter(Demo);
