import Router,{ withRouter } from 'next/router'
import Comp from '../components/comp'


const Demo = ({router})=>{
    return (
        <span>
            <br />
            <br />
            <button onClick={()=>Router.push('/')} >送我去主页</button>
            <br />
            <br />
            {console.log(router.query)}
            <Comp>AAA  {router.query.id}</Comp>
        </span>
    )
}

export default withRouter(Demo)

